mapper.select:
select unb.UNIT_ID, unb.UNIT_CODE,unb.PARENT_ID,unt.NAME,unb.DESCRIPTION,unb.ENABLED_FLAG,unb.MANAGER_POSITION,unb.company_id ,(select unt2.NAME from hr_org_unit_b unb2 LEFT JOIN hr_org_unit_tl unt2 on ( unb2.UNIT_ID=unt2.UNIT_ID and unt2.lang=#{request.locale,jdbcType=VARCHAR}) where unb2.UNIT_ID=unb.PARENT_ID) parent_name, (select pot.NAME from hr_org_position_b pob LEFT JOIN hr_org_position_tl pot on (pob.POSITION_ID=pot.POSITION_ID and pot.lang=#{request.locale,jdbcType=VARCHAR}) where unb.MANAGER_POSITION=pob.POSITION_ID) position_name, (SELECT comt.company_full_name from fnd_company_b comb left JOIN fnd_company_tl comt on(comb.company_id=comt.company_id and comt.lang=#{request.locale,jdbcType=VARCHAR}) where unb.company_id=comb.company_id) company_name from hr_org_unit_b unb LEFT OUTER JOIN hr_org_unit_tl unt ON (unb.unit_id = unt.unit_id AND unt.lang =#{request.locale,jdbcType=VARCHAR})
and unb.unit_id=#{unitId}
and unb.unit_code LIKE concat('%', concat(#{unitCode,jdbcType=VARCHAR}, '%'))
and unb.parent_id=#{parentId}
and unt.name LIKE concat('%', concat(#{name,jdbcType=VARCHAR}, '%'))
and unb.description LIKE concat('%', concat(#{description,jdbcType=VARCHAR}, '%'))
and manager_position =#{managerPosition}
and company_id =#{companyId}
order by unb.${_colName} ${sortorder}
order by unb.unit_id asc
