mapper.select:
select t.PROFILE_VALUE_ID, t.PROFILE_ID, t.LEVEL_ID, t.LEVEL_VALUE, t.PROFILE_VALUE, t.OBJECT_VERSION_NUMBER, t. REQUEST_ID, t.PROGRAM_ID, t.CREATION_DATE, t.CREATED_BY, t.LAST_UPDATED_BY, t.LAST_UPDATE_DATE, t. LAST_UPDATE_LOGIN, t.ATTRIBUTE_CATEGORY, t.ATTRIBUTE1, t.ATTRIBUTE2, t.ATTRIBUTE3, t.ATTRIBUTE4, t. ATTRIBUTE5, t.ATTRIBUTE6, t.ATTRIBUTE7, t.ATTRIBUTE8, t.ATTRIBUTE9, t.ATTRIBUTE10, t.ATTRIBUTE11, t. ATTRIBUTE12, t.ATTRIBUTE13, t.ATTRIBUTE14, t.ATTRIBUTE15,( CASE WHEN t.LEVEL_id='30' THEN (SELECT su.user_name level_Name FROM sys_user su WHERE su.user_id=t.LEVEL_VALUE ) WHEN t.LEVEL_id='20' THEN (SELECT sr.role_code level_Name FROM sys_role_b sr WHERE sr.role_id=t.LEVEL_VALUE ) WHEN t.LEVEL_id='10' THEN t.LEVEL_VALUE END ) level_name from SYS_PROFILE_VALUE t
t.profile_value_id = #{profileValueId,jdbcType=DECIMAL}
AND t.profile_id = #{profileId,jdbcType=DECIMAL}
AND t.level_id = #{levelId,jdbcType=DECIMAL}
AND t.level_value = #{levelValue,jdbcType=VARCHAR}
AND t.profile_value = #{profileValue,jdbcType=VARCHAR}
order by t.profile_value_id
